templates.data:
{{/* vim: set filetype=mustache: */}}
{{/*
Expand the name of the chart.
*/}}
{{- define "chart.name" -}}
{{- default .Chart.Name .Values.nameOverride | trunc 63 | trimSuffix "-" -}}
{{- end -}}

{{/*
Create a default fully qualified app name.
We truncate at 63 chars because some Kubernetes name fields are limited to this (by the DNS naming spec).
If release name contains chart name it will be used as a full name.
*/}}
{{- define "chart.fullname" -}}
{{- if .Values.fullnameOverride -}}
{{- .Values.fullnameOverride | trunc 63 | trimSuffix "-" -}}
{{- else -}}
{{- $name := default .Chart.Name .Values.nameOverride -}}
{{- if contains $name .Release.Name -}}
{{- .Release.Name | trunc 63 | trimSuffix "-" -}}
{{- else -}}
{{- printf "%s-%s" .Release.Name $name | trunc 63 | trimSuffix "-" -}}
{{- end -}}
{{- end -}}
{{- end -}}

{{/*
Create chart name and version as used by the chart label.
*/}}
{{- define "chart.chart" -}}
{{- printf "%s-%s" .Chart.Name .Chart.Version | replace "+" "_" | trunc 63 | trimSuffix "-" -}}
{{- end -}}

{{/*
Common labels
*/}}
{{- define "chart.labels" -}}
helm.sh/chart: {{ include "chart.chart" . }}
{{ include "chart.selectorLabels" . }}
{{- if .Chart.AppVersion }}
app.kubernetes.io/version: {{ .Chart.AppVersion | quote }}
{{- end }}
app.kubernetes.io/managed-by: {{ .Release.Service }}
{{- end -}}

{{/*
Selector labels
*/}}
{{- define "chart.selectorLabels" -}}
app.kubernetes.io/name: {{ include "chart.name" . }}
app.kubernetes.io/instance: {{ .Release.Name }}
{{- end -}}

{{/*
Kubernetes version
Built-in object .Capabilities.KubeVersion.Minor can provide non-number output
For example on GKE it returns "15+" instead of "15"
*/}}
{{- define "chart.KubernetesVersion" -}}
{{- $minorVersion := .Capabilities.KubeVersion.Minor | regexFind "[0-9]+" -}}
{{- printf "%s.%s" .Capabilities.KubeVersion.Major $minorVersion -}}
{{- end -}}

{{/*
Kubernetes resource name prefix
Using the prefix allows deploying multiple instances of the Chart in a single Kubernetes namespace.
If the dynamic resource name prefix is disabled, this template results in an empty string.
As described in `chart.fullname`, the length limit for some resource names is 63.
Therefore, we truncate the prefix to have a max-length of 44 to respect this limit considering the
longest resource name ("db-migrator-install" = 19 chars).
*/}}
{{- define "chart.resourceNamePrefix" -}}
{{- if .Values.dynamicResourceNamePrefixEnabled -}}
{{- (include "chart.fullname" .) | trunc 43 | trimSuffix "-" | printf "%s-" -}}
{{- end -}}
{{- end -}}

apiVersion: v1
kind: Secret
metadata:
  name: {{ include "chart.resourceNamePrefix" . }}db-migrator-config
type: Opaque
stringData:
  tern.conf: |-
    [database]
    host = {{ default (printf "%s-postgresql.%s" .Release.Name .Release.Namespace) .Values.db.host }}
    port = {{ .Values.db.port }}
    database = {{ .Values.db.database }}
    user = {{ .Values.db.user }}
    password = {{ .Values.db.password }}

    [data]
    loadSampleData = {{ .Values.dbMigrator.loadSampleData }}

apiVersion: apps/v1
kind: Deployment
metadata:
  name: {{ include "chart.resourceNamePrefix" . }}hub
  labels:
    app.kubernetes.io/component: hub
    {{- include "chart.labels" . | nindent 4 }}
spec:
  replicas: {{ .Values.hub.deploy.replicaCount }}
  selector:
    matchLabels:
      app.kubernetes.io/component: hub
      {{- include "chart.selectorLabels" . | nindent 6 }}
  template:
    metadata:
      annotations:
        prometheus.io/scrape: "true"
        prometheus.io/path: "/metrics"
        prometheus.io/port: "8001"
      labels:
        app.kubernetes.io/component: hub
        {{- include "chart.selectorLabels" . | nindent 8 }}
    spec:
    {{- with .Values.hub.deploy.readinessGates }}
      readinessGates:
        {{- toYaml . | nindent 8 }}
    {{- end }}
    {{- with .Values.imagePullSecrets }}
      imagePullSecrets:
        {{- toYaml . | nindent 8 }}
    {{- end }}
      {{- if .Release.IsInstall }}
      serviceAccountName: {{ include "chart.resourceNamePrefix" . }}hub
      {{- end }}
      initContainers:
      - name: check-db-ready
        image: {{ .Values.postgresql.image.repository }}:{{ .Values.postgresql.image.tag }}
        imagePullPolicy: {{ .Values.pullPolicy }}
        env:
          - name: PGHOST
            value: {{ default (printf "%s-postgresql.%s" .Release.Name .Release.Namespace) .Values.db.host }}
          - name: PGPORT
            value: "{{ .Values.db.port }}"
        command: ['sh', '-c', 'until pg_isready; do echo waiting for database; sleep 2; done;']
      {{- if .Release.IsInstall }}
      - name: check-db-migrator-run
        image: "bitnami/kubectl:{{ template "chart.KubernetesVersion" . }}"
        imagePullPolicy: IfNotPresent
        command: ['kubectl', 'wait', '--namespace={{ .Release.Namespace }}', '--for=condition=complete', 'job/{{ include "chart.resourceNamePrefix" . }}db-migrator-install', '--timeout=60s']
      {{- end }}
      containers:
        - name: hub
          image: {{ .Values.hub.deploy.image.repository }}:{{ .Values.imageTag | default (printf "v%s" .Chart.AppVersion) }}
          imagePullPolicy: {{ .Values.pullPolicy }}
          {{- if .Values.hub.server.cacheDir }}
          env:
            - name: XDG_CACHE_HOME
              value: {{ .Values.hub.server.cacheDir | quote }}
          {{- end }}
          volumeMounts:
          - name: hub-config
            mountPath: {{ .Values.hub.server.configDir | quote }}
            readOnly: true
          {{- if .Values.hub.server.cacheDir }}
          - name: cache-dir
            mountPath: {{ .Values.hub.server.cacheDir | quote }}
          {{- end }}
          ports:
            - name: http
              containerPort: 8000
              protocol: TCP
          resources:
            {{- toYaml .Values.hub.deploy.resources | nindent 12 }}
      volumes:
      - name: hub-config
        secret:
          secretName: {{ include "chart.resourceNamePrefix" . }}hub-config
      {{- if .Values.hub.server.cacheDir }}
      - name: cache-dir
        emptyDir: {}
      {{- end }}

{{- if .Values.hub.ingress.enabled -}}
apiVersion: networking.k8s.io/v1beta1
kind: Ingress
metadata:
  name: {{ include "chart.resourceNamePrefix" . }}hub
  labels:
    app.kubernetes.io/component: hub
    {{- include "chart.labels" . | nindent 4 }}
  {{- with .Values.hub.ingress.annotations }}
  annotations:
    {{- toYaml . | nindent 4 }}
  {{- end }}
spec:
  backend:
    serviceName: {{ include "chart.resourceNamePrefix" . }}hub
    servicePort: {{ .Values.hub.service.port }}
  {{- with .Values.hub.ingress.rules }}
  rules:
    {{- toYaml . | nindent 4 }}
  {{- end }}
  {{- with .Values.hub.ingress.tls }}
  tls:
    {{- toYaml . | nindent 4 }}
  {{- end }}
{{- end }}

apiVersion: v1
kind: Secret
metadata:
  name: {{ include "chart.resourceNamePrefix" . }}hub-config
type: Opaque
stringData:
  hub.yaml: |-
    log:
      level: {{ .Values.log.level }}
      pretty: {{ .Values.log.pretty }}
    db:
      host: {{ default (printf "%s-postgresql.%s" .Release.Name .Release.Namespace) .Values.db.host }}
      port: {{ .Values.db.port }}
      database: {{ .Values.db.database }}
      user: {{ .Values.db.user }}
      password: {{ .Values.db.password }}
    server:
      allowPrivateRepositories: {{ .Values.hub.server.allowPrivateRepositories }}
      allowUserSignUp: {{ .Values.hub.server.allowUserSignUp }}
      baseURL: {{ .Values.hub.server.baseURL }}
      shutdownTimeout: {{ .Values.hub.server.shutdownTimeout }}
      addr: 0.0.0.0:8000
      metricsAddr: 0.0.0.0:8001
      shutdownTimeout: 30s
      webBuildPath: ./web
      motd: {{ .Values.hub.server.motd }}
      motdSeverity: {{ .Values.hub.server.motdSeverity }}
      basicAuth:
        enabled: {{ .Values.hub.server.basicAuth.enabled }}
        username: {{ .Values.hub.server.basicAuth.username }}
        password: {{ .Values.hub.server.basicAuth.password }}
      cookie:
        hashKey: {{ .Values.hub.server.cookie.hashKey }}
        secure: {{ .Values.hub.server.cookie.secure }}
      csrf:
        authKey: {{ .Values.hub.server.csrf.authKey }}
        secure: {{ .Values.hub.server.csrf.secure }}
      oauth:
        {{- if .Values.hub.server.oauth.github.enabled }}
        github:
          clientID: {{ .Values.hub.server.oauth.github.clientID }}
          clientSecret: {{ .Values.hub.server.oauth.github.clientSecret }}
          redirectURL: {{ .Values.hub.server.oauth.github.redirectURL }}
          scopes: {{ .Values.hub.server.oauth.github.scopes }}
        {{- end }}
        {{- if .Values.hub.server.oauth.google.enabled }}
        google:
          clientID: {{ .Values.hub.server.oauth.google.clientID }}
          clientSecret: {{ .Values.hub.server.oauth.google.clientSecret }}
          redirectURL: {{ .Values.hub.server.oauth.google.redirectURL }}
          scopes: {{ .Values.hub.server.oauth.google.scopes }}
        {{- end }}
        {{- if .Values.hub.server.oauth.oidc.enabled }}
        oidc:
          clientID: {{ .Values.hub.server.oauth.oidc.clientID }}
          issuerURL: {{ .Values.hub.server.oauth.oidc.issuerURL }}
          clientSecret: {{ .Values.hub.server.oauth.oidc.clientSecret }}
          redirectURL: {{ .Values.hub.server.oauth.oidc.redirectURL }}
          scopes: {{ .Values.hub.server.oauth.oidc.scopes }}
        {{- end }}
      xffIndex: {{ .Values.hub.server.xffIndex }}
    email:
      fromName: {{ .Values.hub.email.fromName }}
      from: {{ .Values.hub.email.from }}
      replyTo: {{ .Values.hub.email.replyTo }}
      smtp:
        host: {{ .Values.hub.email.smtp.host }}
        port: {{ .Values.hub.email.smtp.port }}
        username: {{ .Values.hub.email.smtp.username }}
        password: {{ .Values.hub.email.smtp.password }}
    analytics:
      gaTrackingID: {{ .Values.hub.analytics.gaTrackingID }}

apiVersion: batch/v1beta1
kind: CronJob
metadata:
  name: {{ include "chart.resourceNamePrefix" . }}scanner
spec:
  schedule: "15,45 * * * *"
  successfulJobsHistoryLimit: 1
  failedJobsHistoryLimit: 1
  concurrencyPolicy: Forbid
  jobTemplate:
    spec:
      template:
        spec:
        {{- with .Values.imagePullSecrets }}
          imagePullSecrets:
            {{- toYaml . | nindent 8 }}
        {{- end }}
          restartPolicy: Never
          initContainers:
          - name: check-db-ready
            image: {{ .Values.postgresql.image.repository }}:{{ .Values.postgresql.image.tag }}
            imagePullPolicy: {{ .Values.pullPolicy }}
            resources:
              {{- toYaml .Values.scanner.cronjob.resources | nindent 14 }}
            env:
              - name: PGHOST
                value: {{ default (printf "%s-postgresql.%s" .Release.Name .Release.Namespace) .Values.db.host }}
              - name: PGPORT
                value: "{{ .Values.db.port }}"
            command: ['sh', '-c', 'until pg_isready; do echo waiting for database; sleep 2; done;']
          containers:
          - name: scanner
            image: {{ .Values.scanner.cronjob.image.repository }}:{{ .Values.imageTag | default (printf "v%s" .Chart.AppVersion) }}
            imagePullPolicy: {{ .Values.pullPolicy }}
            {{- if .Values.scanner.cacheDir }}
            env:
              - name: TRIVY_CACHE_DIR
                value: {{ .Values.scanner.cacheDir | quote }}
            {{- end }}
            volumeMounts:
            - name: scanner-config
              mountPath: {{ .Values.scanner.configDir | quote }}
              readOnly: true
            {{- if .Values.scanner.cacheDir }}
            - name: cache-dir
              mountPath: {{ .Values.scanner.cacheDir | quote }}
            {{- end }}
          volumes:
          - name: scanner-config
            secret:
              secretName: {{ include "chart.resourceNamePrefix" . }}scanner-config
          {{- if .Values.scanner.cacheDir }}
          - name: cache-dir
            emptyDir: {}
          {{- end }}

apiVersion: v1
kind: Secret
metadata:
  name: {{ include "chart.resourceNamePrefix" . }}scanner-config
type: Opaque
stringData:
  scanner.yaml: |-
    log:
      level: {{ .Values.log.level }}
      pretty: {{ .Values.log.pretty }}
    db:
      host: {{ default (printf "%s-postgresql.%s" .Release.Name .Release.Namespace) .Values.db.host }}
      port: {{ .Values.db.port }}
      database: {{ .Values.db.database }}
      user: {{ .Values.db.user }}
      password: {{ .Values.db.password }}
    scanner:
      concurrency: {{ .Values.scanner.concurrency }}
      trivyURL: {{ .Values.scanner.trivyURL | default (printf "http://%s%s:8081" (include "chart.resourceNamePrefix" .) "trivy") }}
      dockerUsername: {{ .Values.scanner.dockerUsername }}
      dockerPassword: {{ .Values.scanner.dockerPassword }}
      events:
        scanningErrors: {{ .Values.scanner.events.scanningErrors }}

