    - ReadWriteOnce
  resources:
    requests:
      storage: {{ .Values.trivy.persistence.size | quote }}
  {{- if .Values.trivy.persistence.storageClassName }}
  storageClassName: {{ .Values.trivy.persistence.storageClassName }}
  {{- end -}}
